/*
 * @Author: Allen OYang
 * @Email:  allenwill211@gmail.com
 * @Date: 2023-04-18 11:28:09
 * @LastEditTime: 2023-04-18 11:45:46
 * @LastEditors: Allen OYang allenwill211@gmail.com
 * @FilePath: /speak-gpt/src/stores/ChatStore.ts
 */
import { create } from "zustand";
import { persist } from "zustand/middleware";

export interface ChatState {
  /**
   * Chat Components Messages
   */
  chats: string[],
  /**
   *  Textarea Components State
   */
  isRecording: boolean,
  textareaMessage: string,
  selectData: { value: string, label: string }[],
  selectValue: string,
}

export const initialState = {
  chats: [],

  isRecording: false,
  textareaMessage: '',
  selectData: [],
  selectValue: '',
}

const store = () => ({ ...initialState } as ChatState);

export const useChatStore = create<ChatState>()(
  persist(store, {
    name: 'chat-store',
    partialize: (state) =>
      Object.fromEntries(
        Object.entries(state).filter(([key]) => !['foo'].includes(key))
      ),
  })
)
